sys_script.script:
(function executeRule(current, previous /*null when async*/) {
// Add your code here
// rightnow stores the current time
	var rightnow = new GlideDateTime();
	// Create a GlideDateTime object for the When needed date
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	// If the When needed date is before rightnow, do not write the record to the database
	// Output an error message to the screen
	if(whenNeeded.before(rightnow)){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

	var today1 = current.getDisplayValue("u_when_needed");
	var today2 = current.getDisplayValue("u_when_needed");

	if(today1 && today2) { 
		if (gs.dateDiff(today1, today2, true) < 0) { // Compares an today1 and today 2 to see if they are equal 
		gs.addErrorMessage("When needed date cannot be TODAY. Your Request has not been saved to the database.");
		current.setAbortAction(true);
		}
	}
})(current, previous);
